// AboutUs.js
import React from 'react';

const AboutUs = () => {
  return (
    <section className="about-us">
      {<div class="container justify-content-center">
        <h2>About Us</h2>
        <p>
          Welcome to Aptasensor Engineer, your source for mastering the art of
          aptamer-based biosensor design. We are a team of experts in the field
          of biotechnology and diagnostics, dedicated to sharing our knowledge
          and experience with you.
        </p>
        <p>
          Our mission is to empower individuals and professionals with the
          skills and expertise to revolutionize diagnostics, research, and
          healthcare. With a focus on aptamer design, advanced immobilization
          techniques, and signal analysis, our masterclass provides the
          knowledge you need to excel in this field.
        </p>
        <p>
          We are committed to your success and are here to support you on your
          journey to becoming an Aptasensor Engineer. Feel free to
          <a href="contact.html">contact us</a> for any inquiries or to
          <a href="index.html#enroll">enroll in our masterclass</a>.
        </p>
      </div>
      }
    </section>
  );
};

export default AboutUs;
